package com.sinuke.easy;

import org.junit.jupiter.params.ParameterizedTest;
import org.junit.jupiter.params.provider.Arguments;
import org.junit.jupiter.params.provider.MethodSource;

import java.util.stream.Stream;

import static org.junit.jupiter.api.Assertions.assertEquals;

class IsSubsequenceTest {

    @ParameterizedTest
    @MethodSource("testData")
    void isSubsequence(String s, String t, boolean expected) {
        var isSubsequence = new IsSubsequence();
        assertEquals(expected, isSubsequence.isSubsequence(s, t));
    }

    private static Stream<Arguments> testData() {
        return Stream.of(
                Arguments.of("abc", "ahbgdc", true),
                Arguments.of("axc", "ahbgdc", false),
                Arguments.of("", "", true),
                Arguments.of("", "a", true),
                Arguments.of("leetcode", "", true)
        );
    }

}
